  getBitfinexBtcPrice() {
    this.http.get(`https://api.bitfinex.com/v1/pubticker/btcusd`)
      .subscribe(data => {
        let priceData = data.json();
        this.data["bitcoin_rate"] = priceData.bid;
      });
  }
  getFilteredCoinList(input_string = "") {
    input_string = input_string.toUpperCase();
    this.data["filteredList"] = this.data["coins"].filter(function(coin) {
           return coin.MarketName.replace("BTC-","").indexOf(input_string) !== -1;
    });
    this.sortBy(this.type);
  }
  getCoinMarketCapData() {
    this.http.get(`https://api.coinmarketcap.com/v1/ticker/`)
      .subscribe(data => {
        let coinData = data.json();
        this.data["coin_data"] = coinData;
        localStorage.setItem("coin_data",JSON.stringify(coinData));
      });
  }
  openPage(symbol) {
      this.navCtrl.push(CoinPage, {
      symbol: symbol
    });
  }

  sortBy(type) {
    if(type == 1) {
      this.data["filteredList"] = this.data["coins"].sort(function(a,b) {
        return a.MarketName.localeCompare(b.MarketName);
      });
    }
    if(type == 2) {
      this.data["filteredList"] = this.data["coins"].sort(function(a,b) {
        return b.MarketName.localeCompare(a.MarketName);
      });
    }

    if(type == 3) {
      this.data["filteredList"] = this.data["coins"].sort(function(a,b) {
        return a.Ask - b.Ask;
      });
    }
    if(type == 4) {
      this.data["filteredList"] = this.data["coins"].sort(function(a,b) {
        return b.Ask - a.Ask;
      });
    }

    if(type == 5) {
      this.data["filteredList"] = this.data["coins"].sort(function(a,b) {
        return a.Bid - b.Bid;
      });
    }
    if(type == 6) {
      this.data["filteredList"] = this.data["coins"].sort(function(a,b) {
        return b.Bid - a.Bid;
      });
    }

    if(type == 7) {
      this.data["filteredList"] = this.data["coins"].sort(function(a,b) {
        return ((a.Last - a.PrevDay)/a.PrevDay * 100) - ((b.Last - b.PrevDay)/b.PrevDay * 100);
      });
    }
    if(type == 8) {
      this.data["filteredList"] = this.data["coins"].sort(function(a,b) {
        return ((b.Last - b.PrevDay)/b.PrevDay * 100) - ((a.Last - a.PrevDay)/a.PrevDay * 100);
      });
    }

  }

  setSortBy(type) {
    this.type = type;
    this.sortBy(this.type);
  }

  toggleSort(type) {
    if(type == 1) {
      if(this.type == 1) {
        this.setSortBy(2);
      }
      else {
        this.setSortBy(1);
      }
    }

    if(type == 2) {
      if(this.type == 3) {
        this.setSortBy(4);
      }
      else {
        this.setSortBy(3);
      }
    }

    if(type == 3) {
      if(this.type == 5) {
        this.setSortBy(6);
      }
      else {
        this.setSortBy(5);
      }
    }

    if(type == 4) {
      if(this.type == 7) {
        this.setSortBy(8);
      }
      else {
        this.setSortBy(7);
      }
    }
  }


